(()=>{"use strict";(()=>{const e=document.querySelector(".modal-callback"),t=document.querySelector(".modal-overlay");document.addEventListener("click",(o=>{o.target.closest(".fancyboxModal")?(e.classList.toggle("dblock"),t.classList.toggle("dblock")):(o.target.closest(".modal-close")||o.target.closest(".modal-overlay"))&&(e.classList.remove("dblock"),t.classList.remove("dblock"))}))})(),(()=>{const e=document.querySelectorAll('a[href*="#"]');for(let t of e)t.addEventListener("click",(function(e){e.preventDefault();const o=t.getAttribute("href").substr(1);document.getElementById(o).scrollIntoView({behavior:"smooth",block:"start"})}))})()})();